CREATE TABLE PRODUCT(
       Product_ID int primary key,
       subtitle_language varchar(50),
       audio_language varchar(50),      
);

select * from PRODUCT;

CREATE TABLE SEASON(
       Season_NUMBER int primary key,   
);
select * from SEASON;

ALTER TABLE SEASON--add new column to table
ADD favor_rate_of_season float;
select * from SEASON;

update SEASON set favor_rate_of_season=0.5 where Season_NUMBER IN (1, 2, 3); --set new values to table
update SEASON set favor_rate_of_season=0.7 where Season_NUMBER IN (4,5);
update SEASON set favor_rate_of_season=1.0 where Season_NUMBER IN (6, 7, 8);
update SEASON set favor_rate_of_season=0.8 where Season_NUMBER IN (9,10);

select * from SEASON;


