);

select * from DIRECTOR;
CREATE TABLE EPISODE(
       Episode_NUMBER int primary key,
       season_number int,
       FOREIGN KEY (season_number) REFERENCES SEASON(Season_NUMBER),
       
);


SELECT * FROM EPISODE;



CREATE TABLE SERIES(
       Series_ID int primary key,
       imdb float,
       website_url varchar(200),
       topic varchar(100),
       favor_rate float,
       production_year int,
       run_time int,
       season_number int,
       category_id int,
       product_id int,
       FOREIGN KEY (season_number) REFERENCES SEASON(Season_NUMBER), 
       FOREIGN KEY (category_id) REFERENCES CATEGORY(Category_ID), 
       FOREIGN KEY (product_id) REFERENCES PRODUCT(Product_ID), 
       
);






ALTER TABLE SERIES ADD ComputeTotalLikeRate AS (favor_rate + imdb);--computed columns are added
select * from SERIES;

CREATE TABLE MOVIE(
       Movie_ID int primary key,
       imdb float,
       website_url varchar(200),
       genre varchar(50),
       topic varchar(100),
       favor_rate float,
       production_year int,
