       FOREIGN KEY (product_id) REFERENCES PRODUCT(Product_ID), 
       FOREIGN KEY (director_id) REFERENCES DIRECTOR(Director_ID), 
           
       
       
);


select * from MOVIE;
ALTER TABLE MOVIE ADD ComputeTotalLikeRate AS (favor_rate + imdb);--computed columns are added

