--VIEWS


CREATE VIEW [M_CATEGORY] AS
SELECT c.Category_NAME,m.imdb,m.production_year
FROM CATEGORY c,MOVIE m
WHERE m.category_id=c.Category_ID;

SELECT * FROM [M_CATEGORY];

CREATE VIEW [S_CATEGORY] AS
SELECT s.imdb,s.topic,c.favor_rate
FROM SERIES s,CATEGORY c
WHERE s.category_id=c.Category_ID;

SELECT * FROM [S_CATEGORY];

CREATE VIEW [Director_Awards] AS
SELECT Director_NAME, number_of_awards
FROM DIRECTOR
WHERE favor_rate_of_acting_films > 0.5;

SELECT * FROM [Director_Awards];


