--TRIGGERS

CREATE TRIGGER PAYMENTS ON PREMIUM_ACCOUNT
after insert
as
begin 
SELECT TOP(1) payment FROM PREMIUM_ACCOUNT ORDER BY payment DESC						
end



CREATE TRIGGER ORDERED_ACCOUNTS ON ACCOUNT
after insert
as
begin
SELECT TOP(1) ACCOUNT_ID FROM ACCOUNT ORDER BY ACCOUNT_ID DESC						
end


CREATE TRIGGER CATEGORIES_FAVORITE ON CATEGORY
AFTER INSERT
AS
BEGIN
SELECT * FROM CATEGORY
ORDER BY Category_NAME ASC;

END



